def _send_file_like_obj(self, fobj):
    """
    Handles streaming a file-like object to the network.
    """
    while True:
        block = fobj.read(16*1024)
        if not block:
            break

        try:
            self._sock.send(block)
        except TypeError:
            raise ValueError(
                "File-like bodies must return bytestrings. Got: "
                "{}".format(type(block))
            )

    return

