import pyperclip

_ENABLE_CLIP = True
try:
    import pyperclip
    _ENABLE_CLIP = True
except ImportError:
    _ENABLE_CLIP = False

class PivotBrowser:
    def _get_current_func(self):
        func_name = self._select["pivot_funcs"].value
        entity_name = self._select["entities"].value
        return f"{entity_name}.{func_name}()"

    def _copy_to_clip(self, btn):
        del btn
        if _ENABLE_CLIP:
            pyperclip.copy(self._get_current_func())
