import pyperclip

@export
def copy_query_to_clipboard(sql=None):
    """Send query to the clipboard."""

    sql = sql or ""
    message = None

    try:
        pyperclip.copy("{sql}".format(sql=sql))
    except RuntimeError as e:
        message = "Error clipping query: %s." % e.strerror

    return message

