def delete(self, *, recursive=False):
    if self.is_dir:
        if recursive:
            for leaf in self.list(recursive=True):
                _LocalPath(leaf.path).delete()
        self._path.rmdir()
    else:
        kw = {}
        if sys.version_info[:2] > (3, 7):
            kw["missing_ok"] = True
        self._path.unlink(**kw)
