def recv(self, n=0, options=socket.MsgFlag(0)):
    # type: (Optional[int], socket.MsgFlag) -> Optional[_T]
    if self.closed:
        raise EOFError
    if options & socket.MSG_PEEK:
        if self.__queue:
            return self.__queue[0]
        return None
    os.read(self.__rd, 1)
    elt = self.__queue.popleft()
    if WINDOWS and not self.__queue:
        self._winreset()
    return elt

def deliver(self):
    # type: () -> None
    if self.connected:
        try:
            msg = self.fd.recv(65536)
        except socket.error:
            self.stop()
            raise
        if msg:
            self._send(msg)
    else:
        fd, frm = self.fd.accept()
        self._high_send(frm)
        self.fd.close()
        self.fd = fd
        self.connected = True
        self._trigger(frm)
        while True:
            try:
                self.fd.send(self.q.get(block=False))
            except Empty:
                break
