def ConfigureProjectLibBuilder(env):
    _pm_storage = {}

    def _get_lib_license(pkg):
        storage_dir = os.path.dirname(os.path.dirname(pkg.path))
        if storage_dir not in _pm_storage:
            _pm_storage[storage_dir] = LibraryPackageManager(storage_dir)
        try:
            return (_pm_storage[storage_dir].load_manifest(pkg) or {}).get("license")
        except MissingPackageManifestError:
            pass
        return None

    def _correct_found_libs(lib_builders):
        # build full dependency graph
        found_lbs = [lb for lb in lib_builders if lb.is_dependent]
        for lb in lib_builders:
            if lb in found_lbs:
                lb.search_deps_recursive(lb.get_search_files())
        # refill found libs after recursive search
        found_lbs = [lb for lb in lib_builders if lb.is_dependent]
        for lb in lib_builders:
            for deplb in lb.depbuilders[:]:
                if deplb not in found_lbs:
                    lb.depbuilders.remove(deplb)

    def _print_deps_tree(root, level=0):
        margin = "|   " * (level)
        for lb in root.depbuilders:
            title = lb.name
            pkg = PackageItem(lb.path)
            if pkg.metadata:
                title += " @ %s" % pkg.metadata.version
            elif lb.version:
                title += " @ %s" % lb.version
            click.echo("%s|-- %s" % (margin, title), nl=False)
            if int(ARGUMENTS.get("PIOVERBOSE", 0)):
                click.echo(
                    " (License: %s, " % (_get_lib_license(pkg) or "Unknown"), nl=False
                )
                if pkg.metadata and pkg.metadata.spec.external:
                    click.echo("URI: %s, " % pkg.metadata.spec.uri, nl=False)
                click.echo("Path: %s" % lb.path, nl=False)
                click.echo(")", nl=False)
            click.echo("")
            if lb.verbose and lb.depbuilders:
                _print_deps_tree(lb, level + 1)

    project = ProjectAsLibBuilder(env, "$PROJECT_DIR")

    if "test" in env["BUILD_TYPE"]:
        project.env.ConfigureTestTarget()

    ldf_mode = LibBuilderBase.lib_ldf_mode.fget(project)  # pylint: disable=no-member

    click.echo("LDF: Library Dependency Finder -> https://bit.ly/configure-pio-ldf")
    click.echo(
        "LDF Modes: Finder ~ %s, Compatibility ~ %s"
        % (ldf_mode, project.lib_compat_mode)
    )

    project.install_dependencies()

    lib_builders = env.GetLibBuilders()
    click.echo("Found %d compatible libraries" % len(lib_builders))

    click.echo("Scanning dependencies...")
    project.search_deps_recursive()

    if ldf_mode.startswith("chain") and project.depbuilders:
        _correct_found_libs(lib_builders)

    if project.depbuilders:
        click.echo("Dependency Graph")
        _print_deps_tree(project)
    else:
        click.echo("No dependencies")

    return project

def print_terminal_settings(terminal):
    click.echo(
        "--- Terminal on {p.name} | "
        "{p.baudrate} {p.bytesize}-{p.parity}-{p.stopbits}".format(p=terminal.serial)
    )
    click.echo(
        "--- Available filters and text transformations: %s"
        % ", ".join(get_available_filters())
    )
    click.echo("--- More details at https://bit.ly/pio-monitor-filters")
    click.echo(
        "--- Quit: {} | Menu: {} | Help: {} followed by {}".format(
            miniterm.key_description(terminal.exit_character),
            miniterm.key_description(terminal.menu_character),
            miniterm.key_description(terminal.menu_character),
            miniterm.key_description("\x08"),
        )
    )

def get_available_filters():
    return sorted(miniterm.TRANSFORMATIONS.keys())

def download(self, url, checksum=None):
    silent = not self.log.isEnabledFor(logging.INFO)
    dl_path = self.compute_download_path(url, checksum or "")
    if os.path.isfile(dl_path):
        self.set_download_utime(dl_path)
        return dl_path

    with_progress = not app.is_disabled_progressbar()
    tmp_fd, tmp_path = tempfile.mkstemp(dir=self.get_download_dir())
    try:
        with LockFile(dl_path):
            try:
                fd = FileDownloader(url)
                fd.set_destination(tmp_path)
                fd.start(with_progress=with_progress, silent=silent)
            except IOError as exc:
                raise_error = not silent
                if with_progress:
                    try:
                        fd = FileDownloader(url)
                        fd.set_destination(tmp_path)
                        fd.start(with_progress=False, silent=silent)
                    except IOError:
                        raise_error = True
                if raise_error:
                    self.log.error(
                        click.style(
                            "Error: Please read https://bit.ly/package-manager-ioerror",
                            fg="red",
                        )
                    )
                    raise exc
        if checksum:
            fd.verify(checksum)
        os.close(tmp_fd)
        os.rename(tmp_path, dl_path)
    finally:
        if os.path.isfile(tmp_path):
            os.close(tmp_fd)
            os.remove(tmp_path)

    assert os.path.isfile(dl_path)
    self.set_download_utime(dl_path)
    return dl_path

